export const updatePost = ({ id, newImageUrl, newTitle, newContent }) => {
	console.log(id, newImageUrl, newTitle, newContent);
	return fetch(`http://localhost:3005/posts/${id}`, {
		method: 'PATCH',
		headers: {
			'Content-Type': 'application/json;charset=utf-8',
		},
		body: JSON.stringify({
			image_url: newImageUrl,
			title: newTitle,
			content: newContent,
		}),
	}).catch((error) => console.error('Error updating post:', error));
};
