import { useState } from 'react';

import { Icon } from '../../../../components';
import { TableRow } from '../table-row/table-row';

import { useServerRequest } from '../../../../hooks';

import styled from 'styled-components';

const UserRowContainer = ({
	className,
	id,
	login,
	registeredAt,
	roleId: userRoleId,
	roles,
}) => {
	const [selectedRoleId, setSelectedRoleId] = useState(userRoleId);
	const requestServer = useServerRequest();

	const onRoleChange = ({ target }) => {
		setSelectedRoleId(+target.value);
	};

	const onRoleSave = (userId, newUserRoleId) => {
		requestServer('updateUserRole', userId, newUserRoleId);
	};

	const isSaveButtonDisabled = selectedRoleId === userRoleId;
	return (
		<div className={className}>
			<TableRow border>
				<div className="login-column">{login}</div>
				<div className="registered-at-column">{registeredAt}</div>
				<div className="role-id-column">
					<select value={selectedRoleId} onChange={onRoleChange}>
						{roles.map(({ id: roleId, name: roleName }) => (
							<option key={roleId} value={roleId}>
								{roleName}
							</option>
						))}
						<option></option>
					</select>
					<Icon
						id="fa-floppy-o"
						margin="0 0 0 0"
						disabled={isSaveButtonDisabled}
						onClick={() => onRoleSave(id, selectedRoleId)}
					/>
				</div>
			</TableRow>
			<Icon id="fa-trash-o" margin="0 0 0 10px" onClick={() => {}} />
		</div>
	);
};

export const UserRow = styled(UserRowContainer)`
	display: flex;
	margin-top: 10px;

	select {
		font-size: 16px;
		padding: 5px;
	}
`;
