import { setPostData } from './set-post-data';

export const savePostAsync = (requestServer, newPostData) => (dispatch) => {
	return requestServer('savePost', newPostData)
		.then((updatedPost) => {
			dispatch(setPostData(updatedPost.res));
			return updatedPost; // Возвращаем результат после успешного выполнения
		})
		.catch((error) => {
			console.error('Ошибка при сохранении поста:', error);
			throw error; // Пробрасываем ошибку для дальнейшей обработки, если нужно
		});
};
