import { transformPost } from '../transformers';

export const getPosts = async (page, limit) => {
	return fetch(`http://localhost:3005/posts?_page=1&_per_page=2`)
		.then((loadedPosts) => loadedPosts.json())
		.then((loadedPosts) => {
			loadedPosts && loadedPosts.map(transformPost);
		});
};
