import { sessions } from '../sessions';
import { getUser, addUser } from '../api';

export const fetchRoles = async () => {
	const existedUser = await getUser(regLogin);

	if (existedUser) {
		return {
			error: 'Доступ запрещен',
			res: null,
		};
	}

	const roles = await getRoles(regLogin, regPassword);

	return {
		error: null,
		res: roles,
	};
};
