import { transformComment } from '../transformers';

export const getComments = async (postId) => {
	return fetch(`http://localhost:3005/comments?post_id=${postId}`).then(
		(loadedComments) =>
			loadedComments
				.json()
				.then((loadedComments) => loadedComments.map(transformComment)),
	);
};
