export function request(url, method, data) {
	return fetch(url, {
		headers: {
			'content-type': 'application/json',
		},
		method: method || 'GET',
		body: data ? JSON.stringify(data) : undefined,
	})
		.then((res) => JSON.parse(res))
		.catch((error) => console.error('Ошибка при разборе ответа:', error));
}
